SELECT resource.resource_id, resource.title, resource.url, resource.icon, resource.style, resource.classify, resource.parent_id, resource.grouping, resource.sort FROM t_sys_role_authority authority LEFT JOIN t_sys_resources resource ON authority.resource_id = resource.resource_id
AND resource.classify IN ('CATALOG', 'MENU') AND resource.visible = 1 AND resource.`status` = 1 AND authority.role_id = #{role}
ORDER BY sort ASC

SELECT resource.resource_id, resource.title, resource.url, resource.icon, resource.style, resource.classify, resource.parent_id, resource.sort FROM t_sys_role_authority authority LEFT JOIN t_sys_resources resource ON authority.resource_id = resource.resource_id
AND resource.classify IN ('BUTTON') AND resource.visible = 1 AND resource.`status` = 1 AND authority.role_id = #{roleId} and resource.parent_id = #{resourceId}
ORDER BY sort DESC;
